asp <- asp_data %>% 
  group_by(Year, Sex) %>% 
  summarize(AgeAdjRate = sum(ASR * World))

asp %>% 
  group_by(Sex) %>% 
  slice_tail(8) %>% 
  ungroup() %>% 
  tidytable::pivot_wider(
    names_from = "Year",
    values_from = "AgeAdjRate"
  ) %>% 
  gt::gt() %>% 
  gt::opt_vertical_padding(0.5) %>% 
  gt::fmt_number(columns = -1) %>% 
  gt::tab_options(table.width = "100%")
